int a=2;//定义数字接口2 连接a 段数码管int b=3;// 定义数字接口3 连接b 段数码管int c=4;// 定义数字接口4 连接c 段数码管int d=5;// 定义数字接口5 连接d 段数码管int e=6;// 定义数字接口6 连接e 段数码管int f=7;// 定义数字接口7 连接f 段数码管int g=8;// 定义数字接口8 连接g 段数码管int dp=9;// 定义数字接口9 连接dp 段数码管void digital_0(void) //显示数字0{unsigned char i;digitalWrite(g,HIGH);digitalWrite(dp,HIGH);for(i=2;i<=7;i++)digitalWrite(i,LOW);}void digital_1(void) //显示数字1{unsigned char i;digitalWrite(c,LOW);//点亮c 段digitalWrite(b,LOW);//给数字接口3 引脚高电平，点亮b段for(i=5;i<=9;i++)//熄灭其余段digitalWrite(i,HIGH);digitalWrite(a,HIGH);}void digital_2(void) //显示数字2{digitalWrite(a,LOW);digitalWrite(b,LOW);digitalWrite(d,LOW);digitalWrite(e,LOW);digitalWrite(g,LOW);digitalWrite(c,HIGH);digitalWrite(f,HIGH);digitalWrite(dp,HIGH);}void digital_3(void) //显示数字3{  unsigned char i;for( i=2;i<=5;i++)//熄灭其余段digitalWrite(i,LOW);digitalWrite(g,LOW);digitalWrite(f,HIGH);digitalWrite(e,HIGH);digitalWrite(dp,HIGH);}void digital_4(void) //显示数字4{digitalWrite(b,LOW);digitalWrite(c,LOW);digitalWrite(f,LOW);digitalWrite(g,LOW);digitalWrite(a,HIGH);digitalWrite(d,HIGH);digitalWrite(e,HIGH);digitalWrite(dp,HIGH);}void digital_5(void) //显示数字5{digitalWrite(a, LOW);digitalWrite(c, LOW);digitalWrite(d, LOW);digitalWrite(f, LOW);digitalWrite(g, LOW);digitalWrite(b,HIGH);digitalWrite(e,HIGH);digitalWrite(dp,HIGH);}void digital_6(void) //显示数字6{unsigned char i;for(i=4;i<=8;i++)digitalWrite(i,LOW);digitalWrite(b,HIGH);digitalWrite(dp,HIGH);}void digital_7(void) //显示数字7{unsigned char i;for(i=2;i<=4;i++)digitalWrite(i,LOW);for(i=5;i<=9;i++)digitalWrite(i,HIGH);}void digital_8(void) //显示数字8{for( int i=2;i<=8;i++)digitalWrite(i,LOW);digitalWrite(dp,HIGH);}void digital_9(void) //显示数字9{unsigned char i;for(i=2;i<=5;i++)digitalWrite(i,LOW);digitalWrite(f,LOW);digitalWrite(g,LOW);digitalWrite(e,HIGH);digitalWrite(dp,HIGH);}void setup(){int i;//定义变量for(i=2;i<=11;i++)pinMode(i,OUTPUT);//设置2～11 引脚为输出模式digitalWrite(10,HIGH);//digitalWrite(b,LOW);digitalWrite(11,HIGH);}void loop(){while(1){digital_0();//显示数字0delay(1000);//延时1sdigital_1();//显示数字1delay(1000);//延时1sdigital_2();//显示数字2delay(1000); //延时1sdigital_3();//显示数字3delay(1000); //延时1sdigital_4();//显示数字4delay(1000); //延时1sdigital_5();//显示数字5delay(1000); //延时1sdigital_6();//显示数字6delay(1000); //延时1sdigital_7();//显示数字7delay(1000); //延时1sdigital_8();//显示数字8delay(1000); //延时1sdigital_9();//显示数字8delay(1000); //延时1s}}